function y=poly2(x)

% a*(a*(a*(a*(a*(a*(a*(a*(a - 18) + 144) - 672) + 2016) - 4032) + 5376) - 4608) + 2304) - 512

% a^9 - 18*a^8 + 144*a^7 - 672*a^6 + 2016*a^5 - 4032*a^4 + 5376*a^3 - 4608*a^2 + 2304*a - 512
A=[1,-18,144,-672,2016,-4032,5376,-4608,2304,-512];
p=A(1);
for i=2:length(A)
    p=p.*x;
    p=p+A(i);
end
y=p;
end